css:
@-moz-document url-prefix("http://www.nosub.tv/") {
/* nosubの広告などを片っ端から消します お好みで消したりしてお使いくださいね */
/* 広告 */
#lianyue_single_header_ad,#imobile_adspotdiv1,#imobile_adspotdiv2,#imobile_adspotdiv3,#lianyue_top9_header_ad,body > div.wrapper > div.content > div > div.sidebar.slide_js > div.widget_text > div > div,#lianyue_cat_footer_ad {display:none !important;}
/* よくわからない東方キャラ */
body > div.wrapper > div.content > div.margin_bottom.p > div.info > div.mood_comment {display:none !important;}
/* カラフルな線とnotice */
body > div.wrapper > span,body > div.wrapper > div.content > div.margin_bottom.p > span,body > div.wrapper > div.notice,body > div.wrapper > div.content > div.margin_bottom.cms > span {display:none !important;}
/* 下のロゴ */
body > div.wrapper > div.footer {display:none !important;}
/* コメント欄まわり */
body > div.wrapper > div.content > div.margin_top {display:none !important;}
 /* コメントの注意だけ消す */
 body > div.wrapper > div.content > div.margin_top > div.content_710 > div.comments_warning > div.warning {display:none !important;}
/* プレイヤーに出てくるポップアップ */
body > div.wrapper > div.content > div.margin_bottom.p > div.mukioplayer > div.ad_player {display:none !important;}
/*側部の広告*/
#g_floating_area1,#g_floating_area2{display:none !important;}

}
