css:
@namespace url(http://www.w3.org/1999/xhtml);
/* Old method: @-moz-document domain(chatango.com) */
@-moz-document regexp("https?://.+(?!www)\.chatango\.com/.*"){

/* Font settings */
/* -- Message text */
/*    You can add color in depending on image */
P > SPAN:not(.c_username),
.ubdr{
font-family: arial !important;
font-size: 12px !Important;
}

/* Your own text, to better differentiate yourself */
.own-msg:not(.c_username){
font-family: arial !Important;
font-size: 12px !Important;
color: #1d7fb8 !important;
}

/* -- Username text */
/*    Again, add color as necessary */
.c_username{
font-family: arial !important;
font-size: 12px !Important;
/* Text shadow; X, Y, spread, color, can overlap
   by delimiting additional shadow settings with
   commas to use multiple text shadows.

   Most people may adjust spread only. */
text-shadow: 0px 0px 0px rgba(0,0,0,1.0) !important;
}

/* Avatar size */
.user-thumb{
width: 12px !Important;
height: 12px !Important;
}

.user-thumb:hover{
width: 48px !important;
height: 48px !important;
}

/* Chat interface settings */
/* -- Interface background */
/*    You may not want to adjust these. */
#SGBG,
.ubdr,
.sb-icon{
background-color: transparent !Important;
border: none !Important;
}

/* -- Page background */
body,
.message-window{
/*    Location */
background-image: url(http://www.bittonet.com/wp-content/uploads/logo_thumb2.png) !important;
/*    X / Y position (In units of measure or general location) */
background-position: center center !important;
/*    Repeat (no-repeat | repeat-x | repeat-y) */
background-repeat: no-repeat !Important;
/*    There is no reason to touch this. */
background-attachment: fixed !Important;
}

/* -- Chat field background */
.msg{
/*    Adjust depending on image */
background-color: rgba(255,255,255,0.75) !important;
border: none !Important;
}

/* Scrollbars / Scrollbar transparency fix */
/* Adjust to preference */
/* -- Body */
#OMW > DIV:nth-child(2){
background-color: #f1f1f1 !important;
}

/* -- Scroll element */
#OMW > DIV:last-child{
background-color: #bbbbbb !important;
}

/* Stuff to hide */
/* -- Top */
#UTL,
/* -- Bottom */
#FTR_LEFT{
display: none !Important;}

}
