css:
@-moz-document url-prefix("http://caml.inria.fr/pub/docs/manual-ocaml") {
body {
  background-color: #FEFEFE;
  font-family: "Open Sans", sans-serif;
  line-height: 1.5;
  color: #333;
  max-width: 60em;
  margin: 0 auto;
}

a, a:link, a:visited {
  color: #696;
}
a:hover {
  color: #7A7;
  background-color: inherit;
  text-decoration: underline;
}

.part {
  background-color: inherit;
}

.chapter, .section {
  background-color: inherit;
  padding: 2em 0 0;
  font-weight: normal;
  color: #555;
}

.ftoc2 {
  border-left: none;
}

pre {
  background: #F6F6F6;
  border-radius: 6px;
  margin: 1em 2em;
  padding: 1em;
}
}
