css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dhv.onlearning.at") {

@viewport {
width: 400px;
}



    html, body, #head, #main, .container {
        width: 100% !important;
        margin: 0 !important;
        padding: 0 !important;
    }
    div {
        width: auto !important;
    }
    body {
        font-size: 16px !important;
        font-family: 'Verdana';
        overflow-x: hidden;
        background: none !important;
    }
    #main > div {
        width: 100% !important;
        box-sizing: border-box !important;
    }
    #main form {
        font-size: 16px !important;
    }
    #main > * {
       margin: 0 !important; 
    }
    br,
    #head strong {
        display: none;
    }
    #aeroclub {
        display: none !important;
    }
    label {
        margin-left: 30px !important;
    }
    .test_checkbox,
    .answer {
        display: inline-block !important;
        vertical-align: middle !important;
        width: auto !important;
    }
    .test_checkbox {
        margin-top: 10px !important;
        width: 5% !important;
    }
    .answer {
        margin-top: -2px !important;
        width: 95% !important;
        padding-right: 10px !important;
        box-sizing: border-box;
    }
    .sf-menu {
        width: 100%;
    }
    .sf-menu li {
        float: none !important;
        width: 100% !important;
    }
    #head {
        position: absolute;
        top: 100%;
        height: auto !important;
        background: none !important;
        z-index: -1;
    }
    #head > * {
        margin-top: 150px;
    }
    #main,
    #main > strong {
        font-size: 14px !important;
    }
    #main form input {
        font-size: 16px;
        height: auto !important;
        padding: 5px;
    }
    #main form input[type=submit],
    #main form input[type=button]{
        margin-top: 20px;
    }
    #picturecontainer {
        margin-bottom: 15px;
    }
    .container {
        position: relative;
    }
    #main {
        border: 0;
    }
    #WikiStatement {
        margin-top: 80px;
    }
    #main form img {
       max-width: 100%;
       height: auto;
    }
    div + div > a {
        display: none;
    }
}
