@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://togetter.com/li/") {
    #fixed_header, [class*="advertise"], .right,
    .side_category, .ad_custom, form, .side_box, #footer,
    .topics_box, .amazon_reco_box, .favorite_user_box,
    .title_simple_box, .simple_list, .social_widget_box {
        display: none !important;
    }
    
    .contents, .contents .contents_main, .contents .left {
        float: none !important;
        width: 648px !important;
    }
    
    .status {
        height: 1em !important;
    }
    .status_left {
        display: none !important;
    }
}
