css:
@-moz-document domain("vk.com") {
.im_log_act,.im_log_author,.im_log_rspacer {display:none}
tr.im_in {display:block;}
.im_log_body {width:560px !important;float:right}
.im_log_body .wrapped {width:560px!important}
.im_log_author_chat_name {margin:0}
.im_rows .mem_link{font-weight:400!important;color:#000}
.im_rows .mem_link:before {content:'<';display:inline}
.im_rows .mem_link:after {content:'>';display:inline;margin:0 3px 0 0}
.im_date_link:before,.im_fwd_log_date:before {content:'[';display:inline}
.im_date_link:after,.im_fwd_log_date:after {content:']';display:inline}
.im_log_author_chat_name {float:left;padding:0}
.in_msg_text {width:426px}
.im_add_row .im_log_author a, .im_add_row .im_log_date span, .im_add_row .im_log_date a.im_date_link, .im_add_row .im_log_author_chat_thumb, .im_add_row .im_log_author_chat_name {display:inline-block}
.im_log_date {float:left;width:44px !important;margin:2px 0 0}
.im_fwd_log_date {float:left;margin:0 3px 0 0;}
.im_fwd_log_author {display:none;}
.im_log_body  .wrapped.im_fwd_log_wrapped {width:550px!important}
.isotheos_action, .isotheos_action .im_date_link, .isotheos_action .mem_link {color:#d700d7}
.isotheos_action .mem_link:before {content:'* '}
.isotheos_action .mem_link:after {content:' '}
.im_new_msg {background:#edf1f5}
.im_log_t td {border:0 !important}
}
