.ad_entrybox_under,
.ad_side_sky,
.section,
.amazon_ranking,
.amazItem,
div[class="entry amzn_view_checked amzn-taggable-text"],
div[class="entry clfx clr amzn-taggable-text amzn_view_checked"],
