css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("together.jolla.com") {
  body, #header, #secondaryHeader, #ground, #previewer, #searchBar, #ContentRight, .box, .rss {
    background-color: #262626;
    color: #eee;
  }
  
  #searchBar input.searchInput {
    color: #eee;
  }
  
  p, h1, h2, h3, h4, h5, .question-page h1 a, .question-page .post-body {
    color: #eee;
  }
  
  a {
    color: #556b2f !important;
  }
  
  .question-page .post-update-info {
    background-color: #262626;
    background-image: none;
    box-shadow: none;
    color: #eee;
  }
  
  #previewer {
    border: 5px;
    border-color: #eee;
  }
  
  li.tag-left, li.tag-left a.tag, li div.tag-left, li div.tag-left a.tag-right {
    box-shadow: none;
    border-right-color: #464646;
    border-left-color: #464646;
    border-top-color: #464646;
    border-bottom-color: #464646;
    border-image: none;
    border-image-width: 0 0 0 0;
    border: none;
  }
  
  .button {
    background-color: #202020;
  }
  
  .question-page .comments div.comment:hover {
    background-color: #202020;
  }
  
  .post.answer.accepted-answer {
    background-color: #565656;
  }
  
  pre.prettyprint {
    background-color: #666;
  }
  
  pre.prettyprint code span.str {
    color: #030;
  }
  
  pre.prettyprint code span.pln {
    color: #eee;
  }
}
