css:
/* i really want this to be global */
span.spoiler, s, span.markup_spoiler, span.spoiler:hover, s:hover, span.markup_spoiler:hover, div.spoiler, div.spoiler:hover, div.markup_spoiler, div.markup_spoiler:hover {
	color: white !important;
  	background-color: #000000 !important;
}
span.spoiler span.mark, span.spoiler span.mark:hover {
  	color: white !important;
  	background-color: brown !important;
}
span.spoiler a, s a, span.markup_spoiler a, div.spoiler a, div.markup_spoiler a  {
	color: #81a2be !important;
  	background-color: #000000 !important;
}
span.spoiler a:hover, s a:hover, span.markup_spoiler a:hover, div.spoiler a:hover, div.markup_spoiler a:hover {
	color: #D00 !important;
  	background-color: #000000 !important;
}
