css:
@-moz-document domain("played.to") {
/* Minimal Dark Video Player Played.to */
/*            by @pollobrav0           */

body, .menu-content, .clearfix, .fpc {
	background-color: #000;
}

iframe, img, .headerbar, .homelogin, .homelogin2, .footer2, .footer2 a,#OnPlayerBanner,#lb-bg {
  	display:none !important;
}

table {
	margin:auto;
  	position:relative;
}

h1.pagename {
  	color: #191919;
    margin: 0 120px 15px;
  	border-bottom: 2px dotted #e3e3e3;
	font-size: 3em;
  	min-height: 30px;
    font-variant: small-caps;
    font-family: Gill Sans, sans-serif;
    text-align: right;
    text-indent:50px;
	transition: .6s;
}
  
h1.pagename:hover {
    transition: .5s;
  	border-bottom: 2px dashed #e3e3e3;
    color: #aaa;
}

.btn-danger {
  	color:#e3e3e3;
    background-color:#343434;
    background-image: none;
    border-color:#e3e3e3;
}

.btn-danger:hover, .btn-danger a:visited, .btn-danger a:focus {
  	color:#e3e3e3;
    background-color:#525252;
    background-image: none;
    border-color:#aaa;
}

tr:nth-child(2) {
	border-bottom: 2px dotted #e3e3e3;
    border-right: 18px solid #000;
}
}
