css:
@-moz-document domain('www.tumblr.com') {

a.new_post_label > i {
  height: 90px;
}
.new_post_label {
  /*padding: 61px 1px 18px 0 !important;*/
  /*text-transform: lowercase;*/
  /*color: midnightblue !important;*/
}

.new_post_label > I {
  background: url('http://25.media.tumblr.com/tumblr_ma4xdlsy8G1qm1zzjo1_1280.jpg') no-repeat;
  color: transparent !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 3px -1px}
#new_post_label_photo > I {background-position: -72px -1px}
#new_post_label_quote > I {background-position: -145px -1px}
#new_post_label_link > I {background-position: -219px  -1px}
#new_post_label_chat > I {background-position: -292px -1px}
#new_post_label_audio > I {background-position: -365px -1px}
#new_post_label_video > I {background-position: -440px  -1px}

#new_post_label_text:hover > I {background-position: 3px -5px}
#new_post_label_photo:hover > I {background-position: -72px -5px}
#new_post_label_quote:hover > I {background-position: -145px -5px}
#new_post_label_link:hover > I {background-position: -219px  -5px}
#new_post_label_chat:hover > I {background-position: -292px -5px}
#new_post_label_audio:hover > I {background-position: -365px  -5px}
#new_post_label_video:hover > I {background-position: -440px  -5px}

#new_post_label_text:active > I {background-position: 3px -1px}
#new_post_label_photo:active > I {background-position: -73px -1px}
#new_post_label_quote:active > I {background-position: -145px -1px}
#new_post_label_link:active > I {background-position: -219px -1px}
#new_post_label_chat:active > I {background-position: -292px -1px}
#new_post_label_audio:active > I {background-position: -365px -1px}
#new_post_label_video:active > I {background-position: -440px -1px}

}
