css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbs.baozou.com") {
 div.col-md-9.blog-main{
    background-color: #222;
}
    body {
        background: none;
        background-color: #444;
    }
    /* top nav */
    div.container, .baoman-nav, .navbar-default .navbar-collapse {
        background-color: #444;
        border-color: #999;
        color: #fff;
    }
    .navbar-default .navbar-nav > li > a {
        color: #fff;
    }
    .navbar-default .navbar-nav > li > a:hover, .navbar-default .navbar-nav > li > a:focus {
        color: #fff;
    }
    /* sidebar */
    .blog-sidebar {
        background-color: #333 !important;
        color: #fff;
    }
    h1, .h1, h2, .h2, h3, .h3, h4, .h4, h5, .h5, h6, .h6 {
        color: #fff;
    }
    .blog-sidebar p.description {
        background-color: #111;
    }
    /* main */
    .form-control {
        background-color: #333;
        color: #fff;
    }
    div.col-md-9.blog-main, .article-show .blog-post.article .blog-content h2.blog-post-title, .blog-post .blog-content .blog-post-meta li, .article-show .comment-list .blog-post .blog-content p {
        color: #fff !important;
    }
    .slideText ul li a,.slideText ul li a:hover,.slideText ul li a:visited, .slideText ul li a:active {
        color: #6cf;
    }
    .article-show .comment-list-pagination ul > .active > a, .article-show .comment-list-pagination ul > .active > a:hover, .article-show .comment-list-pagination ul > .active > a:focus, .article-show .comment-list-pagination ul > .active > span, .article-show .comment-list-pagination ul > .active > span:hover, .article-show .comment-list-pagination ul > .active > span:focus, div.row ul.pagination li a, .article-show .comment-list-pagination ul > li > a, .article-show .comment-list-pagination ul > li > span,.article-show .comment-list-pagination ul > li > a:hover, .article-show .comment-list-pagination ul > li > a:focus, .article-show .comment-list-pagination ul > li > span:active{
        background-color: #444;
        color: #eee;
    }
    div.row ul.pagination li.active a, .article-show .comment-list-pagination ul > li > span.current , .article-show .comment-list-pagination ul > li > span.current:hover {
        border-color: #6cf;
    }
    .forum-index .blog-post .blog-content .blog-post-title a {
        color: #eee;
    }
    /* search */
    .btn-default , .btn-default:hover, .btn-default:focus, .btn-default:active, .btn-default.active {
        color: #eee;
        background-color: #444;
    }
    /* footer */
    .blog-footer {
        background-color: #444;
        color: #eee;
    }
    a, a:visited, a:hover, a:active {
        color: #6cf;
        text-decoration:none;
    }
}
