css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("allmovie.com") {

head, body, #wrapper, #footer, .results-grid, .nav-item, 
.sub-menu-column, .highlights, .stripe-container, .advertising,
.footer-leaderboard, .most-visited-themes, div#sidebar div#most_visited, .movie-stills, #cmn_wrap, .overflow-container,
.image-holder, .side-details, #main, .tabset a, .headerRelated,
.content-container, .basic-info, .characteristics,
.review, .read-more, .synopsis, .qtip-content, .logged-out-message,
.adv-search-descriptor, .module 
{
background-color: #000000 !important;
}
input, select
{
background: #222222 !important;
}
.sub-menu-column, .most-visited-themes
{
border-left:3px #D30028 solid !important;
border-right:3px #D30028 solid !important;
border-bottom:3px #D30028 solid !important;
}
a, p, dd, dt, h1, h2, h3, h4, h5, .year, .charactText, strong,
.page-title, .text, .category, div, input, select
{
color: #FFFFFF !important;
}
a:hover
{
text-decoration: underline !important;
}
}
