css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url("http://www.jpost.com/") {
    #spot-im-root,
    #menuDiv > ul > li:nth-child(n+2),
    #menuDiv > ul > li:first-child > a,
    .jp-grid-sitewrapper > table:first-child td:nth-child(n+1) *,
    .jp-grid-sitewrapper > table:first-child td:nth-child(n+1) script,
    #ctl00_wrapper_top,
    .block-spacer,
    #home_footer,
    .seoblurb1,
    .line-saperator,
    .block-spacer2
    {
        display: none !important;
    }
    .jp-grid-sitewrapper {
       margin: 0 auto; 
        width: 100% !important;
    }
    .jp-grid-sitewrapper > table:first-child td:nth-child(n+1) {
        width: 0;
    }
    .jp-grid-sitewrapper > table:first-child td:first-child *:first-child {
        display: block !important;
    }
    .jp-grid-sitewrapper > table:first-child {
        width: 330px !important;
        overflow: hidden !important;
        margin: 0 auto;
    }
    #menuDiv,
    #menuDiv *,
    #menuDiv li {
        background: none !important;
        width: auto;
    }
    #menuDiv ul.sf-menu ul {
        position: relative;
        padding: 0;
        margin: 0;
    }
    #menuDiv > ul > li:first-child > ul > li {
        display: inline;
        float:left;
    }

}
