css:
/* dark todoist 
my 1st userstyle for https://todoist.com/app */

@-moz-document url-prefix("https://todoist.com/app") {

body, #content {
	color: #f2f2f2;
	background-color: #202020;}

#td_logo, #td_logo .invisible_bg, #GB_window , .GB_Window .content , .note_content, .note_notes, notes_holder, .notes_gb_window, .notes_gb_window td.caption, .note_item .note_extra_info, .note_item .task_item .task_content_item  {
	background-color: #202020 !important;}

#top_bar, #editor {
	background-color: #202020;
}

li.current, li:hover, .task_item:hover .content, .task_item:hover .assign_user, .task_item:hover .menu, .task_item:hover .project, .on_menu_show, #amicomplete_floater, .gb_iframe_html, .GB_Window .caption, .GB_Window .close {
	background-color: #555555 !important;
}

.AmiMenu, .AmiMenu .no_hover td {
	background-color: #202020 !important;
}

.input_q, .input_on_focus, .dp_cal, .dp_container, .dp_cal select {
	color: #f2f2f2 !important;
	background-color: #202020 !important;
	border: solid 1px #555555 !important;
}

.scheduler_holder .today_icon span {
color: #f2f2f2;	
}

.section_header a, .project_link {
color: #fff;
}

.GB_overlay {
background-color: #2E2E2E;}

.date_overdue {
border-bottom: 1px solid #d8422d !important;
}

.date {
color: #d8422d;
}
}
