css:
@-moz-document domain(news.ycombinator.com) {

font[color] {
    color:#DD0000;
}
font[color="#000000"]{
    color:#000000;
}

a:visited { color:#0099AA;}

td, body {
    color: rgb(0,0,0);
}

td[bgcolor="#ff6600"]{
    background-color:#000000;
}

.pagetop, td[bgcolor="#ff6600"] a{
    color: #ffffff !important;
}

[src="y18.gif"] {
    display: none;
}

[bgcolor="#f6f6ef"]{
    background-color: transparent;
    width: 90%;
}

}
