css:
@-moz-document domain(uctok.com) {
body { font-size: 12px; padding-left: 20px; padding-right: 20px; }
body > a { display: none; }
table[width="780"], body > table { width: 100%; }
table[width="800px"] { width: 100%; max-width: 1024px }
#slider { margin: 0 auto; display: none }
.nav { font-size: 14px; }
.postbody { font-size: 13px; }
.mainmenu, p, td, .genmed, .topictitle { font-size: 14px; }
.forumlink { font-size: 16px; }
a.forumlink { color: #DEC048 }
a.gen, a.genmed, a.gensmall, a.nav { color: #E2B777; }
.nav > a:first-child { color: #EFD8B2 !important; }
.gensmall, .table0 { font-size: 12px; }
.mainmenu > a { display: inline-block; height: 21px; line-height: 20px; }
.mainmenu > img { display: inline; margin-top: 4px }
.row1 + .row1 > .genmed + .gensmall { display: none; }
.forumline td + td tr + tr + tr + tr { display: none; }
}
