css:
@-moz-document domain("footballidentity.com") {
.bookmarks-container {
margin-top:35px;
}

.logo {
margin-left:auto;
margin-right:auto;
width:393px;
}

.level2-selected, .level2, .twolevelmenu{
-webkit-border-top-right-radius: 15px;
-moz-border-radius-topright: 15px;
border-top-right-radius: 15px;    
width: 803px;   
}

.topInfoContainer{
background: 0;
background-color: #FFF;
}

.basicContentHolder{
-webkit-border-bottom-right-radius: 15px;
-webkit-border-bottom-left-radius: 15px;
-moz-border-radius-bottomright: 15px;
-moz-border-radius-bottomleft: 15px;
border-bottom-right-radius: 15px;
border-bottom-left-radius: 15px;
}
      
.site {
margin:0;
width:100%;
}

.srcBanners,.sidebar-container,.itemtd:last-child,.fid-credits-wrapper {
display:none;
}

.srcContent {
margin-left:auto;
margin-right:auto;
}

.topPanel {
width:99.4%;
}

body {
background: -moz-linear-gradient(top, #459162 0%, #459162 53%, #006e2e 100%); /* FF3.6+ */
background: -webkit-gradient(linear, left top, left bottom, color-stop(0%,#459162), color-stop(53%,#459162), color-stop(100%,#006e2e)); /* Chrome,Safari4+ */
background: -webkit-linear-gradient(top, #459162 0%,#459162 53%,#006e2e 100%); /* Chrome10+,Safari5.1+ */
background: -o-linear-gradient(top, #459162 0%,#459162 53%,#006e2e 100%); /* Opera 11.10+ */
background: -ms-linear-gradient(top, #459162 0%,#459162 53%,#006e2e 100%); /* IE10+ */  
background-repeat:repeat-x;
background-color:#006e2e;
}
}
