css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("emberjs.com") {
    
body {
        background: #222;
    }
#sidebar ol#toc-list
{
        background-color:#323232;
}
#sidebar #toc-list  {
        background-color:#333;
        color:#aaa;
}
#sidebar ol#toc-list li.level-1 > a {
        background-color: #333;
        color:#E1563F;
}
#sidebar ol#toc-list li > ol li.sub-selected > a {
        background-color: #333;
        color:#aaa;
}
#sidebar ol#toc-list li.level-2 a, #sidebar ol#toc-list li.level-3 a {
        background-color: #444;
        color:#aaa;
}

#sidebar ol#toc-list li.level-2 a:hover, #sidebar ol#toc-list li.level-3 a:hover {
        background-color: #555;
        color:#eee;
}
.api .class-info p {
        color: #E1563F;
}

.api .description p {
        color: #aaa;
}

h2,h3 {
        color: #ccc;
}

body.guides code, body.blog code, body.api code {
        color: #fff;
}
    
}
