css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("developer.mozilla.org") {

  #main-header, .home-hacks, .home-contribute, .wiki-main-content{
    background-color: #888;
  }
  
  .home-demos{
    background-color: #333;
  }
  footer, .toggleable, .toggle-container, .toggler {
    background-color: #222;
  }
 
  .card-grid > li {
     background-color: #222;
  }
  #content, #document-main, #toc {
    background-color: #444;
    color:#eee
  }
  
  #toc, .summary, .geckoVersionNote p {
    background-color: #222;
    color:#eee
  }
  
  a:link{
    color:#99f99f;
  }
  
  a:visited{
    color:#affaff;
  }
  
  a:hover{
    text-decoration:none !important;
    background:#000;
  }
  
  #main-nav .submenu {
    background-color: #333;
  }
  
  
}
