@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("guerretribale.fr") {

    
    a{color:#303030;}
    a:hover{color:#7F0200;}
    .forum:hover.selected a,.modemenu td.selected:hover a{color:#7F0200;text-decoration:underline;}
   .forum{border:1px solid #fff;}
   .forum:hover{border:1px solid #7F0200;background:#303030}
    .forum:hover a{color:#FF0400;}
    .btn,.btn-default,.modemenu td,.modemenu td a,span.forum,.forum a{transition:all 0.3s ease;}
    .modemenu td:hover{background:#303030;}
    .modemenu td:hover a{color:#FF0400}
    .btn,.btn-default{
        background: #7F0200;
        box-shadow:0em 0em 0.3em #000 inset;
        color:#fff;
        border-radius:0;
        border:none;
        padding:1em;
    }
    .btn:hover,.btn-default:hover{
        background: #BC0300;
        text-shadow:0em 0em 0.1em #fff;
        color:#fff;
    }
    #show_outgoing_units .vis tr:hover td,#show_outgoing_units .vis tr:hover td a{background:#303030;color:#fff}
   .top_shadow{background:transparent;top:30px;box-shadow:0px 2em 0.5em #7F0200;}
    .build_options .float_right{}
    #menu_row > .menu-item,.top_bar,#menu_row > td.menu-item > a,.top_bar .bg_left,.top_bar .bg_right{background:linear-gradient(to top,#4F100F,#7F0200);transition:all 0.3s ease;}
    #menu_row > td.menu-item > a{background:linear-gradient(to top,#4F100F,#7F0200);
        box-shadow:
0px 2px 2px 0px rgba(0, 0, 0, 0.5),
0px 2px 2px 0px rgba(0, 0, 0, 0.5) inset;
        height:40px;left:0px;margin-left:-2px;}
    #topContainer{top:-2px;}
    
    
    #menu_row > .menu-item:hover,#menu_row > td.menu-item:hover > a{background:#350A0A;
    box-shadow:
0px 2px 2px 0px rgba(0, 0, 0, 0.5) inset,
0px 2px 2px 0px rgba(0, 0, 0, 0.5);}

    
    
    .menu_column tr .menu-column-item a,.menu_column tr .menu-column-item,.menu_column tr:first-child .menu-column-item a,.menu_column tr:first-child .menu-column-item, .menu_column tr:first-child .menu-column-item a,#topdisplay .menu_column .menu-column-item a,#topdisplay .menu_column tr:first-child .menu-column-item a{background:linear-gradient(to top,#4F100F,#7F0200);}
    
        .menu_column tr .menu-column-item a:hover,.menu_column tr .menu-column-item:hover,.menu_column tr:first-child .menu-column-item a:hover,.menu_column tr:first-child .menu-column-item:hover,#topdisplay .menu_column .menu-column-item a:hover,#topdisplay .menu_column tr:first-child .menu-column-item a:hover{background:#350A0A;}
    
#topdisplay .menu_column .menu-column-item a, .menu_column .menu-column-item a {left:-22px;}    
    
    .menu_column tr .bottom {display:none;}
    
   
     body{background:#111; }
