css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix("https://www.tumblr.com/") {
.new_post_label {
  padding: 78px 1px 8px 0 !important;
  font-size: 8pt !important;
  text-transform: lowercase;
}

.new_post_label > I {
  background: url('http://i.imgur.com/mMK0owt.png') no-repeat !important;
}

.new_post_label > I {
  color: transparent !important;
  text-shadow: none !important;
  transition: background-position .08s linear !important;
}
#new_post_label_text > I {background-position: 2px 2px !important}
#new_post_label_photo > I {background-position: -73px 2px !important}
#new_post_label_quote > I {background-position: -143px 2px !important}
#new_post_label_link > I {background-position: -218px 2px  !important}
#new_post_label_chat > I {background-position: -293px 2px !important}
#new_post_label_audio > I {background-position: -376px 2px !important}
#new_post_label_video > I {background-position: -451px 2px  !important}

#new_post_label_text:hover > I {background-position: 2px -2px !important}
#new_post_label_photo:hover > I {background-position: -73px -2px !important}
#new_post_label_quote:hover > I {background-position: -143px -2px !important}
#new_post_label_link:hover > I {background-position: -218px -2px  !important}
#new_post_label_chat:hover > I {background-position: -293px -2px !important}
#new_post_label_audio:hover > I {background-position: -376px -2px  !important}
#new_post_label_video:hover > I {background-position: -451px -2px  !important}

#new_post_label_text:active > I {background-position: 2px 2px !important}
#new_post_label_photo:active > I {background-position: -73px 2px !important}
#new_post_label_quote:active > I {background-position: -143px 2px !important}
#new_post_label_link:active > I {background-position: -218px 2px !important}
#new_post_label_chat:active > I {background-position: -293px 2px !important}
#new_post_label_audio:active > I {background-position: -376px 2px !important}
#new_post_label_video:active > I {background-position: -451px 2px !important}
}
