css:
@-moz-document url-prefix('http://www.petnebula.com/') {

table, td {
  background:none;
  background-color: #7fbcd8 !important;
  border:none;
}

body {
background-color: #FEFEFE !important;
  color:#0f0f0f
}

b {
  color:#000000 !important;
}

font {
  color:#FFFFFF !important;
}
font:hover {
    color:#0f0f0f !important;
}
a:hover {
  cursor:pointer !important;
  background-color:#4891c9;
}

tbody > tr > td[bgcolor = "#7b0000"] {
    background-color:#4891c9 !important;
}

}
