#cnn_ftrcntnt {
  background: #ccc !important;
  padding-right: 5px !important;
  padding-left: 5px !important;
}

#cnn_ftrcntntinner {
  width: 950px !important;
  padding-right: 5px !important;
  padding-left: 5px !important;
}

div#storycontent {
  background-color: #EBEBE4 !important;
}

#cnnContentContainer, .cnn_storyarea {
  padding-left: 5px !important;
  padding-right: 5px !important;
}

.cnn_strycntntlft p {
  font-size: 1.39em !important;
