@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.sickipedia.org") {
  body {
    font-size: 60% !important;
  }
  .jokeRatingButtons img {
    width: 30px;
  }
  .jokeSocialButtons {
    display: none !important;
  }
  .newsLetterBox,
  .contentColumnHeader >h2 {
   display:none !important;
  }
  
  .clickable {
    pointer-events: none;
    cursor: default;
  }
  .jokeAuthor,
  .jokeRatingButtons {
    pointer-events: initial;
    cursor: pointer;    
  }
  #announcementBox {
    display: none;
  }
}
