css:
@-moz-document domain("news.ycombinator.com") {
  * {
     font-family: 'Open Sans', 'Verdana', sans-serif !important;
     color: #353942 !important;
  }
  
  body center > table:nth-child(1) tr:nth-child(n+2) a:visited {
    color: #606878 !important;
  }
  
  .comment, td.title {
     font-size: 14pt;
  }
  
  body > center:nth-child(1) > table:nth-child(1) {
    width: 600px;
  }
  
  body, 
  body center > table:nth-child(1) {
    background: none repeat scroll 0% 0% #E6E6E6;
  }
}
