css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://myfreeshares.com/pages"), url-prefix("https://myfreeshares.com/pages"), 
url-prefix("https://www.myfreeshares.com/pages"), 
url-prefix("http://www.myfreeshares.com/pages"){
  body {
     background-color: #4bc5f9 !important;
     color: #000 !important;
  }
  a:link, a:hover, a:visited {
     color: #0800fa !important;
  }
  
  center > table > tbody > tr:nth-child(2) {
     background-color: #ffffff;
  }

  center > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td > table > tbody > tr > td {
       color: #000000;
  }

  td[bgcolor="black"] {
       color: #ffffff ! important;
  }
}
