css:
@-moz-document url-prefix("http://www.tumblr.com/"), url-prefix('https://www.tumblr.com/')   {

.new_post_label > I {

  color: transparent !important;

  transition: background-position .08s linear !important;

}



  .new_post_label/*_icon*/ > I {

    background-image: url("http://i60.tinypic.com/149qhj9.png") !important;

    background-size: auto !important;

    z-index: -1;

  }

  .new_post_label:nth-of-type(1) > /*SPAN*/I {background-position: 2px 0 !important}

  .new_post_label:nth-of-type(2) > /*SPAN*/I {background-position: -73px 0 !important}

  .new_post_label:nth-of-type(3) > /*SPAN*/I {background-position: -148px 0 !important}

  .new_post_label:nth-of-type(4) > /*SPAN*/I {background-position: -223px 0 !important}

  .new_post_label:nth-of-type(5) > /*SPAN*/I {background-position: -298px 0 !important}

  .new_post_label:nth-of-type(6) > /*SPAN*/I {background-position: -373px 0 !important}

  .new_post_label:nth-of-type(7) > /*SPAN*/I {background-position: -448px 0 !important}

  .new_post_label > /*SPAN*/I {

    top: 5px !important;

    -webkit-transition: top 0.08s linear !important;

    transition: top 0.08s linear !important;

  }

  .new_post_label:hover > /*SPAN*/I {top: 1px !important}

  .new_post_label {padding: 66px 0 13px 1px !important}



}
