css:
@-moz-document url-prefix("http://themittani.com") {
body,
.tmc_block,
.featured-frontgrid li,
.panel-pane,
.front .panel-pane.pane-frontpage-article-grid-panel-pane-1 ul.pager,
.cat-listing-wrapper .views-row,
ul.pager {
	background: #EEE;
	color: #1A1A1A;
	border-color: #6A9DC5 !important;
}

.featured-frontgrid .views-field-field-content-body, 
.featured-frontgrid .views-field-field-summary-content, 
.featured-frontgrid .views-field-field-video-description,
#content-cat-article .pane-node-field-main-content, 
#content-cat-media .pane-node-field-main-content, 
#content-cat-article .field-name-field-main-content, 
#content-cat-media .field-name-field-main-contentpager {
	color: #1A1A1A;
}

a, a:link, a:visited, a:active,
.front .panel-pane.pane-frontpage-article-grid-panel-pane-1 ul.pager li a,
ul.pager li a{
	color: #0C73C0 !important;
}

.featured-frontgrid .views-field-title,
.panel-col-last .pane-title,
#content-cat-article .panel-col-first .pane-news-feature-minipanel .pane-node-field-spotlight-image + .panel-separator, 
#content-cat-media .panel-col-first .pane-media-minipanel .pane-node-field-spotlight-image + .panel-separator,
.cat-listing-wrapper .views-field-title {
	border-color: #1A1A1A;
}

.pane-view-spotlight-panel-pane-1 .views-field-title, 
#content-cat-article .pane-node-title,
.featured-frontgrid .views-field-type,
#content-cat-article .panel-col-first .panel-pane .pane-node-title, 
#content-cat-media .panel-col-first .panel-pane .pane-node-title {
	background: rgba(255, 255, 255, 0.7);
}
}
