css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("mibbit.com") {
select,
textarea,
input{
-moz-appearance: none !important;
}
.infobar,
#footer{
display:none !important
}
.inputdiv,
.iescrollfix2,
.userlistdiv,
.ulinfo,
#footerinner{
margin:0 !important
}
.ulinfo,
#footerinner,
#tabs,
#tabs > div,
#header{
padding:0 !important
}
.ulinfo{
height:auto !important;
}
.inputdiv{
position:fixed !important;
left:0 !important;
bottom:0 !important;
}
.generaltab{
bottom:37px !important;
top:97px !important
}
.iescrollfix2,
.userlistdiv{
top:16px !important;
bottom:37px !important;
display:inline-block !important;
/*position:relative !important;*/
bottom:0px !important;
height:auto !important;
border-width:1px 0 !important;
}
.iescrollfix2{
width:80% !important;
}
.userlistdiv{
width:20% !important;
}
}
