css:
@-moz-document url-prefix("https://talkgadget.google.com") {

/* Container for the chats */
.iN {
  background-color: white;
}

/* Common class for each message row */
.tk {
    padding-top: 0px;
    padding-bottom: 0px;
    border-style: solid;
    border-width: 1px 0 0 0;
    border-color: rgba(0,0,0,0.05);
}

/* Message container from me */
.tk.pj {
    padding-right: 4px;
    padding-left: 42px;
}

/* Messages row from me */
.pj>.Tn {
    float: left;
}

/* Messages bubble from me */
.pj>.Tn>.KL {
    padding-left: 4px;
    float: left;
}

/* Timestamps */
.TlvAYc {
    padding-top: 0px;
}

/* Timestamps for my messages */
.pj>.Tn>.TlvAYc {
    text-align: left;
    left: 13px;
    right: auto;
}

/* Timestamps for other people */
.Sn>.Tn>.TlvAYc {
    left: 13px;
}
/* The little chat triangle thing */
.ci {
    display:none;
}
  
/* Read receipts */
.mK {
  display: none;
}

/* My image */
.Up.zo {
    left: -32px;
    top: 9px;
    width: 32px
}

/* Other people's image */
.Up.pC {
    top: 9px;
}

/* Message content within container */  
.PD {
    padding-bottom: 0px;
    -webkit-box-shadow: 0;
    box-shadow: none;
}

/* Byline */
.QD {
  margin-top: 0px;
  padding-top: 0px;
}

.Qm.jU {
      top: 0px;
}

/* Bottom receipt + who's typing */
.kT {
    height: 34px;
    top: -10px;
}
      
}
