css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("cnet.com"),
domain("cnetnews.com.cn"),
domain("cnetstatic.com"),
domain("cnetfrance.fr"),
domain("cnet.com.au"),
domain("cnet.co.uk"),
domain("cnet.de")
{
#secondary, #rbContent, #rbFooter, .topicList li:hover,
.searchFilters, #article-body, .row,
.livefyreContainer, .livefyreContainer h3 span,
.tocNav li, .tocNav .topStoriesHeading h5, .tocNav ul
{
background-color: #000000 !important;
}
#rb_skin, #primarySearch, iframe, #rbSkin, .searchFilters 
{
background: #000000 !important;
}
.topCategories, .assetText, .current, .selected, 
.toggleComments, .tocNav, .tocNav li a
{
background: #AA1801 !important;
}
.product-single-left-rail-quick-specs
{
background: #111111 !important;
}
a, p, h1, h2, h3, h4, h5, #publisher-name,
.product-landing-quick-specs-row-label, .date,
.product-landing-quick-specs-row-content,
.product-landing-quick-specs-ratings-label,
.thumb-description, .section, .dek, .credit,
.assetText span, .pullQuote, .caption,
.pullQuote div, #primarySearch, .hed, 
.description, li, .topicType, .pageNav, 
.topicPageNav, .fyre-comment-count span,
.livefyreContainer, .livefyreContainer span,
.formattedTime
{
color: #FFFFFF !important;
text-shadow: none !important;
}
a:hover
{
color: #FFFFFF !important;
text-shadow: none !important;
text-decoration: underline !important;
}
}
