css:
@-moz-document url-prefix('http://b.hatena.ne.jp/') {
	#hatena-bookmark-user-page p.entry-summary,
	#hatena-bookmark-user-page .entry-data,
	#hatena-bookmark-user-page .entry-image-block,
	#hatena-bookmark-user-page #recommend-user,
	#hatena-bookmark-user-page #right-container,
	#hatena-bookmark-user-page .username{
		display:none !important;
	}

	#hatena-bookmark-user-page .entry-block.entry-feature-image{
		min-height: 5px !important;
		padding-left: 10px !important;
	}

	#hatena-bookmark-user-page #center-container{
		width:700px !important
	}

	#hatena-bookmark-user-page ul.entry-comment img.profile-image{
		width:16px !important;
		height:16px !important;
	}

	#hatena-bookmark-user-page img.service-icon{
		top:10px !important;
	}

	#hatena-bookmark-user-page ul.entry-comment div.header,
	#hatena-bookmark-user-page ul.entry-comment div.comment{
		display:inline !important;
	}

	#hatena-bookmark-user-page a.user-tag{
		color:#2D6BC4;
	}
}
