css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("versijos.lt") {

  /* hide ads */

  ins.adsbygoogle,
  .textwidget,
  .margin-30b,
  #top-block-bg .span9,
  #mainmenu-block-bg
  {
    display: none !important;
  }

  .box-content {
    padding: 0 0 10px;
  }

  .widget-title {
    margin-top: 0;
  }
  
  /* decrease header */
  
  #top-block-bg {
    padding-top: 0;
  }
  
  #logo {
    margin: 0;
    padding-top: 10px;
  }
  
  #logo img {
    height: 45px;  
  }
  .top-menu-block,
  .top-menu-block div {
    display: inline;
    border: none;
    position: absolute;
    width: 0;
  }
  
  ul#social-icons {
    position: absolute;
  }
  
  ul#social-icons li {
    background-color: transparent;
    border: none;
    display: block;
    width: 20px;
    padding: 0 5px;
  }
  
  /* decrease spacing */
  
  #wide-sidebar {
    padding: 10px 0;
  }
  
  .margin-25t.news-ticker {
    margin-top: 10px;
  } 
}
