css:
/* 
    v1.5
*/

@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.universetoday.com") {
    
    /*Background*/
    body, .page, #content, .authorsure-author-box, body div,
    ul, li, iframe, div, .menu a, .login_alert, .single-in {
        
        background-color: black !important;
    }
    
    .wp-caption, .share-email a, .share-print a, .share-facebook a, .share-twitter a, .share-end a, .email, .required, .form-control{
        background-color: #222222 !important;
    }
    
    /*Text*/
    body, a, p, h1, .comment_author, .num_comments, .sd-title, .wc-comment-text{
    
       color: #bbddbb !important;
    }
    
    /*Border*/
    * {
        border-color:  #333333 !important;
    }
    
    /*Hide content*/
    .ut_ad_content {
        display: none !important;
    }
    
    #header {
        overflow: hidden;
        height: 92 px;
        width: 897 px;
    }
}
