css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("bbc.co.uk") {

  a[href*="news/magazine"] + *::before,
  a[href*="news/blogs-magazine"] + *::before {
    content: "Magazine / Blog / Click bait";
    display: block !important;
    font-size: inherit !important;
    background: #202020;
    color: #fff;
    padding: 1px 3px;
    border-radius: 2.5px;
  }
  
  a[href^="http://www.bbc.co.uk/news/magazine-"], a[href^="http://www.bbc.co.uk/news/blogs-magazine-"], a[href^="/news/magazine/"], a[href="/news/magazine"], a[href^="/news/magazine-"], a[href^="/news/blogs-magazine"], a[href^="http://www.bbc.co.uk/news/blogs-trending-"], a[href^="/news/blogs-trending-"], a[href^="http://www.bbc.co.uk/news/blogs/trending/"], a[href^="/news/blogs/trending/"] { display: none !important; }

}
