css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("rockpapershotgun.com") {
#featured-stories-wrapper, #sidebar, #footer,
#menu-utility-container, #menu-main-container,
#ecd_opt_in_banner, .social, #skyscraper, .OUTBRAIN,
.featured-block-title, .tags
{ display:none !important; }

html, body, #content, body.home div.content, #page-wrapper { background:#000 !important; float:none !important;
    margin:auto !important; border:none !important; }
p, li, .commentlist { font-family:sans-serif !important; line-height:1.4em !important; }

#leaderboard, #page, #header-content, div.low-leaderboard-container { width:auto !important; }
div.content, #content { width:750px !important; border:none !important; }
#page { margin:auto !important; }

div.content > .block, #all-comments { margin-bottom:1em !important; border-radius:0.5em !important; border:none !important; }
div.block img { border-radius:5px !important; }

#leaderboard { overflow:hidden !important; }
div.low-leaderboard-container, .post-inner h2, .post-inner p.info { text-align:center !important; 
    float:none !important; display:block !important; }
div.low-leaderboard-container, #comments { border:none !important; margin-bottom:1em !important; }
.post-inner p.info { line-height:1em !important; margin-right:0 !important; }
.post-inner h2 { margin-bottom:0 !important; font-size:2.2em !important }
.post-inner iframe, .post-inner object { margin:auto !important; display:block !important; }
#header { background:none !important; box-shadow:none !important; position:static !important; 
    margin:1em auto 0 auto !important; }

#header .logo a { background:url("http://www.rockpapershotgun.com/images/09/september/rps.jpg") no-repeat !important;
    height:76px !important; width:600px !important;
    display:block !important; position:static !important; margin:auto !important; }

}

@-moz-document  url-prefix("http://www.rockpapershotgun.com/forums/") {

#toplinks { position:static !important;}

h2 { font-size:14pt !important; font-style:normal !important; }

#centeredmenu, #footerRPS { display:none !important }

div.content { width:auto !important; }

}

@-moz-document  url-prefix("http://www.rockpapershotgun.com/wp-") {

body { background:inherit !important; }
form p:first-child { display:inherit !important; }
blockquote { border:inherit !important; background:inherit !important; }

}
