css:
@namespace url("http://www.w3.org/1999/xhtml");

@-moz-document domain("loups-garous-en-ligne.com") {

    body {
        background : #030714 url("http://www.zupimages.net/up/14/34/d9s2.png") center top no-repeat fixed !important ;
        background-size : 100% auto !important ;
    }

/*Bannière*/    
    #header img[src="stuff/newheader.png?7"] {display : none !important ;}
    
    #menu {margin-top : 85px !important ;}
    
    .imgInscription {
        margin-left : 720px !important ;
        margin-top : -50px !important ;
    }
    
/*Top 10*/    
    #boxRed, #boxContactUs {
       background-color : #030714 !important ;
       background-image: -webkit-linear-gradient(top right,#030714, #000206) !important ;
       background-image: -moz-linear-gradient(top right,#030714, #000206) !important ;
       background-image: -ms-linear-gradient(top right,#030714, #000206) !important ;
       background-image: -o-linear-gradient(top right,#030714, #000206) !important ;
       background-image: linear-gradient(top right,#030714, #000206) !important ;
    }


/*Forum*/
    img[src*="imageset/logo.png"] {display : none !important ;}
    
    #phpbb #header {height : 285px !important ;}

}
