css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("goodanime.net"), domain("goodanime.eu"), domain("goodanime.co") {
    html, #page, div, #cat-col-1.cat_col > li, #cat-col-2.cat_col > li, #cat-col-3.cat_col > li, * {background: rgb(25, 25, 25)!important;}
    #wrapper, .premiumdll, .postcontent, .wprp_wrapper {border: 1px solid rgb(25, 25, 25)!important;}
    #mini-announcement > h1, #content > .post > table, #page > #wrapper > #content > table, #content > p, .post > iframe:nth-child(3), .topad, .rsidebar, #content > iframe, div#VelBar {display: none}
    #header > .navi, #header > #top-search, #page > #header {margin: 0px 0px 0px -5px; top: 10px; height: 0px;}
}
