css:
@-moz-document domain("osu.ppy.sh") {
    
    .bodytopbg, div.header.clickable-cell,
    .bmlist-options > a, .icon-comments
    {
        background: rgb(25, 25, 25);
    }
    
    .beatmapListing > tbody > tr > th,
    .bmlist-options,
    .content-infoline, #playstats,
    .historyp,
    .beatmapListing th,
    #tablist a,
    .tablebg.forum_table > tbody > tr > td.row1, .tablebg.forum_table > tbody > tr > td.row2,
    .frozen-header,
    div#mw-head-base, div#mw-page-base, .profileStatLine, .profileStatHeader, #searchArea a.selected, #searchArea a:hover
    {
        background: rgb(30, 30, 30);
    }
    
    .beatmap, .beatmap:hover, .tablebg .row1:hover, .tablebg .row2:hover, .profileGameModes a, .profile-tabs td, .changelog_area, input, input:hover, input:focus
    {
        background: rgb(35, 35, 35);
        border: 1px solid rgb(70, 70, 70);
    }
    
    .content-with-bg, .footer, .pack-msg,
    #tablist A:hover,
    .sf-menu a:hover, .sf-menu a:focus, .sf-menu li.sfHover
    {
        background: rgb(40, 40, 40);
    }
    
    .dialog-area, .peppynote, .reasonpanels > li, .reasonpanels > li:hover > i,
    .changelog-text,
    .row1p,
    li.sfHover > ul > li > a,
    .login-dropdown,
    .blurb, .news-text, .row1,
     .postRight, .postbody, .postTime, .tablebgside > tbody > tr > td, .quotecontent, .sectionContents, .playstyle-container
    {background: rgb(60, 60, 60)}
    
    .final_footer,
    .row2p,
    div#tablist, table#songinfo, #songinfo td.colour, .posttext,
    .row2,
    .postLeft,
    div.mw-panel, .prof-beatmap
    {
        background: rgb(50, 50, 50);
    }
    
    .beatmapListing tr.row1p:hover, .beatmapListing tr.row2p:hover, .beatmap .tags a:hover
    {
        background: rgb(80, 80, 80)
    }
    
    #toc, table.wikitable, .wikitable th
    {background-color: rgb(50, 50, 50); background: rgb(50, 50, 50)}
    
    .beatmap .tags a {background: rgb(60, 60, 60); color:black; border: rgb(70, 70, 70);}
    
    .row1p > td > a, .row2p > td > a, .row1p > td > a:hover, .row2p > td > a:hover
    {color: rgb(170, 170, 170);}
    
    #songinfo td.colour, #songinfo td.colour > a, #songinfo > tbody > tr > td > a,
    .login-dropdown > form > div.smaller > a,
    .blurb, .news-text, .row1 > td > a, .row2 > td > a,
    .postTime > a,
    div.mw-panel div.portal h5, div.mw-panel div.portal div.body ul li a
    {color: black;}
    
    #songinfo td{border-bottom: 1px solid rgb(80, 80, 80)}
    
    #tablist A.active:link, #tablist A.active:visited {background: rgb(10, 10, 10)}
    
    sf-menu a{color:#77297D;}
    
    a, .sf-menu a:visited, .sf-menu a, #general > font, .beatmap .title:hover
    {color:rgb(168, 51, 138);}
    
    .blurb, .news-text, .main-box{border: 1px solid rgb(70,70,70)}

    .blog-post{background:none}
    
    .content-with-bg > h1, .content-with-bg > h2, .login-open-button, .login-open-button:hover, .new-user-button:hover,
    .ntitle,
    .row1, .row2,
    .forum_posts_table tr:hover, h1, .prof-beatmap .pp-display, h2, .profileGameModes a:hover
    {text-shadow: none}
    
    div#coolheader {display: none;}
    .beatmap .small-details, .changelog_area, input, input:focus {color:rgb(100,100,100);}
    input:focus {box-shadow:none}
    #tablist, #songinfo {border-bottom:1px solid rgb(168,51,138)}
    .posttext {border:1px solid rgb(70,70,70)}
}
