}

@-moz-document domain("raisport.rai.it") {
#direct-link {background-color: #333!important; box-shadow:none!important; border:1px solid #333!important; font-family: "Arvo"!important;font-size: 15px!important;color: #FFF!important; font-weight: 700!important; text-transform: uppercase; width: 628px!important; margin: 0px!important}
#direct-link a{font-family: "Arvo"!important;font-size: 15px!important;color: #FFF!important; font-weight: 700!important; text-transform: uppercase;}
#direct-link a:hover{font-family: "Arvo"!important;font-size: 15px!important;color: #FAD52A!important; font-weight: 700!important; text-transform: uppercase;}
