css:
@-moz-document regexp('.*vk.*com.*widget_comments.*php.*app=.*') {
#wcomments_posts {margin-left: -60px;}
div.reply_link_wrap, div.reply_box, div.info_footer, .reply_image, div.post_like_wrap, div.reply_fakebox_wrap {display: none;}
div.wall_text div, div.reply_text div {display: inline;}
.wall_module .reply_text {max-width: 100% !important;}
a.author {font-weight: bold !important; color: #aaa;}
#wcomments_posts div, #wcomments_posts .wcomments_post {border: none; padding-top: 0 !important; padding-bottom: 0 !important; margin-top: 0; margin-bottom: 0;}
}
