css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("forum.noxworld.ru") {
  /* Лишние элементы */
  #modbuttons_bottom, .titlebg2, #forum_name img, #footerarea, .windowbg2 p, .icon, .infocenter_section p.section, h3.catbg img, h3.catbg2 img, ul.reset li fieldset, ul.postingbuttons li a img, .messageicon, .keyinfo h5, #infocenterframe h3.catbg { display: none !important; }

  /* Название форума в шапке */
  #forum_name a::after { content: "NoxWorld.ru | Forum"; }

  /* Вид форумных сообщений */
  .avatar { max-width: 160px !important; max-height: 160px !important; }
  .poster { text-align: center; }
  .keyinfo { min-height: 0em !important; }

  /* Вид списка форумов */
  table.boardsframe td.info h4 { min-height: 20px; }
  .infocenter_section div.sectionbody { margin-left: 0px !important; }
  .categoryframe { margin-top: 1em; }

  /* Основное меню */
  .main_menu { background: url('http://forum.noxworld.ru/Themes/NoXWorldBis/images/gradient.jpg') repeat-x scroll 0% 0% #1F1F1F !important; border: 2px solid #adadad; border-top: 0; }
  .main_menu, .main_menu ul, .main_menu li, .main_menu li a span, .main_menu li.last a span, .main_menu li.active a span em, .main_menu li.active a span, .main_menu li.last.active { padding: 0 !important; }
  .main_menu ul { line-height: 2em; }
  .main_menu ul, .main_menu li.active a, .main_menu li a span, .main_menu li.last a span, .main_menu li.active a span em, .main_menu li.active a span, .main_menu li.last.active { background: none !important; }
  .main_menu li.active a, .main_menu li a { padding: 0 7px !important; }
}
