css:
@-moz-document url-prefix("https://www.youtube.com/"), url-prefix("http://www.youtube.com/") {
/* YOUTUBE MIN
   BY DAYLO
   VER 0.7
      more ad hunt
	  padding fixes
	  more shadows
	  less shadows
	  cool buttons
*/

/* just a small fix */
video, #movie_player, .watch-small {
  overflow-y: hidden !important;
  padding-right: 0;
  margin-right: 0;
}

/* ==Masthead== */

/* =ticker= */
#ticker {
  background: #fff!important;
  color: #000!important;
}
/* =semitrans= */
#yt-masthead-container {
  background-color: rgba(255,255,255,0.7)!important;
  border-bottom: 0!important;
}

/* =hide notifs= */
#sb-button-notify {
  display: none;
}

/* =hide guide= */
#appbar-guide-button-container {
  display: none !important;
}

/* =hide country flag= */
span.content-region {
  display: none;
}

/* =button shadows= */
button.yt-uix-button-default, .yt-uix-button-default[disabled], .yt-uix-button-default[disabled]:hover, .yt-uix-button-default[disabled]:active, .yt-uix-button-default[disabled]:focus {
  border: 0!important;
}

button.yt-uix-button-default:hover, .not-watch8 .yt-uix-button-expander:hover, .yt-uix-button-text:hover {
  border: 0!important;
}
#upload-privacy-selector {
  font-size: xx-large;
  height: 160px;
  width: 90px;
}

/* ==Front Page== */

/* =hide big ad= */
#ad_creative_iframe_1, #feed-pyv-container, #header {
  display: none;
}
/* ="appbar" tidy= */
#appbar-content, .guide-pinned.show-guide .guide-pinning-enabled #page, .guide-pinned.show-guide .guide-pinning-enabled #appbar-content {
  padding-left: 0!important;
}
#masthead-appbar {
  background-color: rgba(255,255,255,0.7)!important;
}
ul.appbar-nav-menu {
  background-color: rgba(255,255,255,0.7)!important;
}
/* ==Main Tidy== */

/* =Hide sidebar= */
div.watch-sidebar, .branded-page-v2-secondary-col, .branded-page-related-channels {
  display: none;
  width: 0px!important;
  max-width: 0px!important;
}
#appbar-guide-menu {
  display: none!important;
}

/* =modify main width= */
div.watch-content {
  width: 100%!important;
}
div.watch-small {
  padding-left: calc(100% - 854px * 2);
  padding-right: 0!important;
}
div.not-watch8 #watch7-headline, .not-watch8 #watch7-notification-area {
  padding: 15px 20px 9px;
}
div#alerts {
  display: none;
}

/* =modify comment width= */
div.comments-iframe-container {
  width: 100%;
}
#comments-test-iframe {
  width: 100% !important;
}
iframe[id^="I0_"] {
  width: 100% !important;
}
#watch-discussion .comments-iframe-container {
  max-width: 100%;
}
/* ==Video Tidy== */

div.ytp-action-buttons {
  display: none;
}
div.html5-info-bar, div.html5-player-chrome {
  background: rgba(0,0,0,0.8);
}
div.ytp-unloaded-progress {
  background: rgba(0,0,0,0.8);
}
div.ytp-load-progress {
  background: rgba(130,130,130,0.8);
}
div.ytp-play-progress {
  background: rgba(180,0,0,0.8);
}
/* ==Search== */

#masthead-search {
  max-width: 100% !important;
}
#yt-masthead .yt-masthead-logo-container {
  width: 85px!important;
}

/* ==Channel== */

/* =header= */
div.channel-header .primary-header-contents {
  position: absolute;
  padding: 3px 0 0 15px;
}
#channel-subheader ul, #channel-navigation-menu {
  float: right!important;
}
/* =Channel Header shadows= */
ul.about-secondary-links, .about-custom-links {
  background-color: transparent!important;
  text-shadow: 0px 0px 6px rgba(0, 0, 0, 1);
}
img.channel-header-profile-image {
  box-shadow: none;
}

/* ==Upload== */

#upload-hub-footer {
  display: none;
  height: 0;
}

/* ==Footer== */
#footer-container {
  display: none;
}
#content-container, #body-container {
  padding-bottom: 0;
}
}
