css:
@-moz-document domain(mixedmartialarts.com) {

	.GenericText > :first-child, .ThreadList, .StandardForum .PostList, .StandardForum .BannerLeftCol {
		width: 100% !important;
	}

	.data_row td, .data_row_alt td {
		vertical-align: middle !important;
	}

	.data_row .First, .data_row_alt .First {
		width: 50% !important;
	}

	.StandardForum .Paging {
		padding: 0 10px !important;
		background-image: none !important;
		background-color: #ddd !important;
		height: 32px !important;
		line-height: 32px !important;
		font-size: 14px !important;
	}

	.StandardForum .Paging a {
		padding: 0 3px !important;
	}
	
	.StandardForum .Paging img {
		display: none !important;
	}

}
