css:
@-moz-document url-prefix("https://mel.np.edu.sg/webapps/") {
#navFrame {
	height: 31px !important;
	position: fixed;
	opacity: 0.2;
	z-index: 100;
	transition: all .3s cubic-bezier(0.645, 0.045, 0.355, 1);
  	background: rgba(0, 0, 0, 0.5);
}

#navFrame:hover {
	opacity: 0.8;
}

.brandingImgWrap {
	display: none;
}

#appTabList {
	float: none;
}

.appTabs .hideoff {
	display: none;
}

#contentFrame {
	position: absolute;
	height: 100% !important;
}

body > .locationPane:first-child {
	margin-top: 40px !important;
}

body > .breadcrumbs {
	margin-top: 31px !important;
}

.topTabs, .topFrame {
	box-shadow: none !important;
	height: auto !important;
	background: transparent !important;
}

.appTabs a {
	padding: 0.5em;
	margin: 0 0.5em;
}

.tabWrapper-right {
	padding: 0;
}
}
