css:
@-moz-document domain("youku.com"), domain("soku.com") {
* { background-color: #111 ! important; }
/*背景色*/

div,
a,
li,
ul,
span { background: transparent! important; }
/*背景透明*/

* { font-weight: bolder! important; }
/*字体加粗*/

* { border-width: 0px! important; }

* { border-style: none! important; }
/*无边框*/

#module_basic_relationleft,
.sideCol,
.yk-logo,
.g-view,
.g-box,
.vpactionv5_iframe_wrap,
.s_main page-editor,
.g-footer,
a.video-pv,
.ad-flag-wrap ad_area,
#module_basic_sub,
.mainCol { display: none! important; }
/*去广告*/

div { color: #d09 !important; }
/*字体颜色*/

a { color: #d90 !important; }
/*字体颜色*/

span { color: #9d0 !important; }
/*字体颜色*/
}
