css:
@-moz-document domain(habrahabr.ru), domain(habr.ru), domain(geektimes.ru), domain(toster.ru), domain(megamozg.ru) {
.post .content,
.question__text {
	font-size: 1.6em !important;
	max-width: 35em
}
.html_format {
	line-height: 1.5em  !important;
}
.html_format,
.html_format h1,
.html_format h2,
.html_format h3,
.html_format h4,
.html_format h5,
.question__text,
.answer__text {
	font-family: Georgia, serif  !important;
}
.question__text pre,
.answer__text pre,
.html_format pre {
	margin-top: 1em !important;
	font-size: 1rem;
}
.question__text a,
.answer__text a,
.html_format a {
	font-size: 1em;
}
.comment_body .html_format,
.answer__text,
.answer__text * {
	max-width: 43em;
}
.comment_body .html_format {
	font-size: 1.2em !important;
}
.answer__text,
.answer__text * {
	font-size: 1em !important;
	max-width: 43em;
}
}
