css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("nextinpact.com")
{ 
    /*centrage corp*/
    header
    {
        padding-left: 8% !important;
    }
    
    .top-abo{
        background-color: transparent;
    }
  
    #page-container
    {
        margin-top: 60px;
    }
  
    /*réglages text*/
    #color_title
    {
        font-size: 30px !important;
    }
    
    .unit-article h1 
    {
        font-size: 32px !important;
    }

    .unit-article .soustitre_article 
    {
        font-size: 12px !important;
    }
    
    .article_content, .actu_chapeau, p
    {
        font-family: Arial,Verdana !important;
    }
    
    #container_article 
    {
        font-size: 15px;
    }
    
    /*marges*/
    .actu-head
    {
        margin-top: 15px;
        margin-bottom: 30px;
    }
    
    p.actu_chapeau
    {
        margin-top: 35px;
    }
    
    h3
    {
        margin-top: 30px;
        margin-bottom: 16px;
        font-size: 18px !important;
    }
    
    /*suppression de la section inférieure à l'article*/
    #extra-services
    {
        display: none !important;
    }
    
    /*lettrine*/
    .actu_chapeau:first-letter, .bonplan-article div#container_article:first-letter, .longcontent-article div#container_article:first-letter, .first-letter-up:first-letter 
    {
        margin: 8px 3px 0px 0px !important;
        font-size: 38px;
    }
    
    .article_content h3 
    {
        font-size: 16px;
        font-family: Arial,Verdana !important;
    }
    
    
    /*centrage du corps principale*/
    #body 
    {
       padding-left: 8% !important;
    }
    
    /*autres*/    
    *[class *="content_pub"]
    {
        display: none !important;
    }
    
    @media (max-width: 1200px)
    {
        #sideBarArticle
        {
            display: none;
            width: 0;
        }

        header, .content-wrapper
        {
            width: auto;
        }

        #body, .main-content
        {
           padding: 0% !important;
           width: 100% !important;
        }
        
    }
    
}

@-moz-document url-prefix("https://www.nextinpact.com/multi/") {
    .actu-head{
        margin-top: -3px !important;
    }
}
