css:
@-moz-document url-prefix("https://www.burning-seri.es") {
html { background: #e539d1; }
#user { background-color: #c647c4; }
nav > ul { background-color: #c647c4; }
.home > .column > section, .home > #teaser { background-color: #b348c9; -webkit-box-shadow: 0 0 3px #b348c9; /* Generated using PoTTiis BSC Script */ -moz-box-shadow: 0 0 3px #b348c9; }
nav > ul > li:hover, nav > ul > li > ul { background-color: #c647c4; }
.home li:nth-child(even) { background-color: #a947c6; }
body > #root { -webkit-box-shadow: 0 0 5px #b348c9; box-shadow: 0 0 5px #b348c9; -moz-box-shadow: 0 0 5px #b348c9; /* HeaderBild 900x169 */ background: #fff url('https://b-s.cc/img/header.v4.jpg') no-repeat; }
.home #shoutbox dl > dt { background-color: #a947c6; }
}
